import collections

class Solution:
    def longestPalindrome(self, s: str) -> str:
        result = []
        for i in range(len(s)):
            result.append(self.palindrome_string(i, i, s))
            result.append(self.palindrome_string(i, i + 1, s))
        return max(result, key=lambda x: len(x))

    def palindrome_string(self, left: int, right:int, s:str) -> str:
        while left >= 0 and right < len(s) and s[left] == s[right]:
            left -= 1
            right += 1
        return s[left + 1:right]



if __name__ == "__main__":
    s = Solution()
    print(s.longestPalindrome("babad"))
    print(s.longestPalindrome("cbbd"))
    print(s.longestPalindrome("a"))
    print(s.longestPalindrome("ac"))

    # case of time limit exceeded
    print(s.longestPalindrome(""))

    print(s.longestPalindrome("cbbd"))
